class NewsRepository extends EntityRepository
{
	public function findActifsNews()
	{
		$query = $this->createQueryBuilder('ne')
				->where('current date BETWEEN ne.dateDebut and ne.dateFin')
				->orderBy('ne.id', 'DESC');
				//->setMaxResults($limit);
		return $query->getQuery()
					->getResult();
	}
